{% extends "base.html" %}
{% load static %}

{% block content %}

<h1>Image Tagger</h1>

{% if show_export_button %}
    <a href="{% url 'export_tagged_csv' %}" class="btn btn-primary mb-3">üì• Download CSV</a>
{% endif %}

<form id="tagging-form" method="get" action="{% url 'tag_images' %}">
    <label for="batch_tags">Batch filter:</label>
    <input type="text" name="batch_tags" id="batch_tags" value="{{ request.GET.batch_tags|default:'' }}" class="form-control mb-3">
    <button type="submit" class="btn btn-secondary mb-3">Filter</button>
</form>

<div class="d-flex flex-wrap gap-3">
