            <li class="page-item">
                <a class="page-link" href="?page={{ page_obj.next_page_number }}{% if request.GET.batch_tags %}&batch_tags={{ request.GET.batch_tags }}{% endif %}">Next</a>
            </li>
        {% endif %}
    </ul>
</nav>

<script id="tag-data-json" type="application/json">
    {{ tag_data|json_script:"tag-data-json" }}
</script>

<script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>
<script>
    const tagData = JSON.parse(document.getElementById('tag-data-json').textContent);

    $(function() {
        $('.tag-checkbox').each(function() {
            let checkbox = $(this);
            let imageId = checkbox.data('image-id');
            let tag = checkbox.data('tag');

            if (tagData[imageId] && tagData[imageId].includes(tag)) {
                checkbox.prop('checked', true);
            }
        });

        $('.tag-checkbox').change(function() {
            let checkbox = $(this);
            let imageId = checkbox.data('image-id');
            let tag = checkbox.data('tag');
            let checked = checkbox.is(':checked');

            $.ajax({
                url: "{% url 'save_tag_ajax' %}",
                method: "POST",
                contentType: "application/json",
                data: JSON.stringify({
                    image_id: imageId,
                    tag: tag,
                    checked: checked
                }),
                headers: {
                    'X-CSRFToken': '{{ csrf_token }}'
                },
                success: function(response) {
                    console.log('Tag saved:', response);
                },
                error: function(xhr) {
                    console.error('Error saving tag:', xhr.responseText);
                }
            });
        });
    });
</script>

{% endblock %}
