# 1) chunks_and_instructions.py  
**Input:** `age6_sentence.jsonl` (one JSON per *sentence*, in the `memory` schema we used)  
**Output:** `age6_sft.jsonl` (instruction‚Äëtuning pairs)

```python
#!/usr/bin/env python3
"""
Read sentence-level memory JSONL, group into small 'event' chunks,
and emit multiple instruction/response pairs per chunk for SFT.

Usage:
  python chunks_and_instructions.py age6_sentence.jsonl age6_sft.jsonl \
      --min-sent 2 --max-sent 6
"""

import sys, json, argparse, itertools
from typing import List, Dict, Any

def load_jsonl(path: str) -> List[Dict[str, Any]]:
    with open(path, "r", encoding="utf-8") as f:
        return [json.loads(line) for line in f if line.strip()]

def save_jsonl(path: str, rows: List[Dict[str, Any]]):
    with open(path, "w", encoding="utf-8") as f:
        for r in rows:
            f.write(json.dumps(r, ensure_ascii=False) + "\n")

def same_bucket(a: Dict, b: Dict) -> bool:
    """Heuristic: contiguous sentences belong together if they share any of (age, year, location)."""
    am, bm = a.get("memory", {}), b.get("memory", {})
    ages_a, ages_b = am.get("ages") or [], bm.get("ages") or []
    years_a, years_b = am.get("years") or [], bm.get("years") or []
    locs_a,  locs_b  = am.get("locations") or [], bm.get("locations") or []
    # overlap test
    if set(ages_a) & set(ages_b): return True
    if set(years_a) & set(years_b): return True
    if set(locs_a)  & set(locs_b):  return True
    # otherwise not "same bucket"
    return False

def chunk_sentences(rows: List[Dict], min_sent=2, max_sent=6) -> List[List[Dict]]:
    """
    Greedy pass: group contiguous sentences into small chunks.
    Start a new chunk when metadata diverges OR chunk reached max_sent.
    If a leftover < min_sent, merge it into previous chunk.
    """
    chunks = []
    cur = []
    for i, r in enumerate(rows):
        if not cur:
            cur = [r]
            continue
        if len(cur) >= max_sent:
            chunks.append(cur)
            cur = [r]
            continue
        # same bucket with previous sentence?
        if same_bucket(cur[-1], r):
            cur.append(r)
        else:
            chunks.append(cur)
            cur = [r]
    if cur:
        chunks.append(cur)
    # fix tiny tail chunks
    fixed = []
    for ch in chunks:
        if len(ch) < min_sent and fixed:
            fixed[-1].extend(ch)
        else:
            fixed.append(ch)
    # cap size again after merges
    rebalanced = []
    for ch in fixed:
        for i in range(0, len(ch), max_sent):
            part = ch[i:i+max_sent]
            if len(part) >= min_sent or not rebalanced:
                rebalanced.append(part)
            else:
                rebalanced[-1].extend(part)
    return rebalanced

def join_text(chunk: List[Dict]) -> str:
    return " ".join(c["memory"]["text"].strip() for c in chunk if c.get("memory", {}).get("text"))

def merge_meta(chunk: List[Dict]) -> Dict[str, Any]:
    mm = {"ages": set(), "years": set(), "locations": set(), "people": set(), "themes": set()}
    for c in chunk:
        m = c.get("memory", {})
        for k in mm:
            mm[k].update(m.get(k, []))
    return {k: sorted(v) for k, v in mm.items()}

def sft_item(system: str, user: str, assistant: str = "", tags=None) -> Dict[str, Any]:
    return {
        "system": system,
        "messages": [
            {"role":"user","content": user},
            {"role":"assistant","content": assistant}
        ],
        "tags": tags or []
    }

SYSTEM_ARCHIVIST = "You are a careful archivist for Toddric. Be concise, accurate, and neutral."
SYSTEM_COACH     = "You are Toddric, a practical, kind coach. Use short, concrete steps and avoid therapy/legal advice."

def build_prompts(chunk_text: str, meta: Dict[str, Any]) -> List[Dict[str, Any]]:
    """
    Emit 3 instruction formats per chunk (facts JSON, summary+lessons, advice-in-voice).
    Assistant content is left empty for human/assisted labeling later, or to train with DPO/SFT if pre-labeled.
    """
    facts_user = (
        "Extract key facts from the memory below. Return JSON with: "
        "summary, who, where, when, notable_events, emotions, consequences.\n\n"
        f"Memory:\n{chunk_text}\n\nMetadata hint: {meta}"
    )
    summary_user = (
        "Summarize this memory in ‚â§3 sentences, then list 3 lessons as bullets. "
        "Keep it specific to the text, avoid speculation.\n\n"
        f"Memory:\n{chunk_text}"
    )
    advice_user = (
        "Given this memory, produce 3 short pieces of practical advice I can use today. "
        "Keep each to one sentence. Avoid therapy or legal advice.\n\n"
        f"Memory:\n{chunk_text}"
    )
    return [
        sft_item(SYSTEM_ARCHIVIST, facts_user, "", tags=["memories","facts_json","v1"]),
        sft_item(SYSTEM_ARCHIVIST, summary_user, "", tags=["memories","summary_lessons","v1"]),
        sft_item(SYSTEM_COACH,     advice_user, "", tags=["memories","advice_coach","v1"]),
    ]

def main():
    ap = argparse.ArgumentParser()
    ap.add_argument("input_sentence_jsonl")
    ap.add_argument("output_sft_jsonl")
    ap.add_argument("--min-sent", type=int, default=2)
    ap.add_argument("--max-sent", type=int, default=6)
    args = ap.parse_args()

    rows = load_jsonl(args.input_sentence_jsonl)
    if not rows:
        print("No input rows.")
        sys.exit(1)

    # Keep original order; assume input is already sentence-split and sequential
    chunks = chunk_sentences(rows, min_sent=args.min_sent, max_sent=args.max_sent)

    out = []
    for ch in chunks:
        text = join_text(ch)
        meta = merge_meta(ch)
        out.extend(build_prompts(text, meta))

    save_jsonl(args.output_sft_jsonl, out)
    print(f"‚úÖ Wrote {len(out)} SFT items from {len(chunks)} chunks ‚Üí {args.output_sft_jsonl}")

if __name__ == "__main__":
    main()
```

