# **1. Cleaned-up `views.py` (for `tagger`)**

```python
from django.shortcuts import render, redirect
from django.core.paginator import Paginator
from django.conf import settings

from images.models import Image

def get_untagged_image_ids(batch_size=20):
    """
    Get IDs of untagged images from Redis.
    """
    untagged_ids = list(settings.REDIS_CLIENT.smembers('imgtag:untagged'))
    return untagged_ids[:batch_size]

def tagger(request):
    """
    Main image tagging view. Handles both batch and individual tag saves.
    Relies on Redis for storing and retrieving tag info.
    """
    redis_client = settings.REDIS_CLIENT
    if request.method == "POST":
        image_ids = request.POST.getlist('selected_images')
        tags = request.POST.getlist('batch_tags')  # tags from batch select2
        for image_id in image_ids:
            redis_client.sadd(f'imgtag:{image_id}', *tags)
            redis_client.sadd('imgtag:tagged', image_id)
            redis_client.srem('imgtag:untagged', image_id)
        return redirect('tagger')

    # GET: show next batch of untagged images
    untagged_ids = get_untagged_image_ids(batch_size=20)
    images = Image.objects.filter(id__in=untagged_ids).order_by('id')
    paginator = Paginator(images, 20)
    page_number = request.GET.get('page')
    page_obj = paginator.get_page(page_number)

    # Get tag info for Select2 dropdown (from Redis or static list)
    # Example: Load all tags ever used (from Redis set), or hardcode
    all_tags = sorted(list(settings.REDIS_CLIENT.smembers('imgtag:alltags')))  # or replace with a static list
    all_tags_for_select2 = [{"id": t, "text": t} for t in all_tags]

    tag_data = {}
    for img in page_obj.object_list:
        tag_data[img.id] = list(redis_client.smembers(f'imgtag:{img.id}'))

    # Optionally, batch tags for the current session/batch (for Select2)
    current_batch_tags = request.GET.get('batch_tags', '')
    if current_batch_tags:
        current_batch_tags = [t for t in current_batch_tags.split(',') if t]
    else:
        current_batch_tags = []

    total_count = Image.objects.count()
    total_untagged = len(list(settings.REDIS_CLIENT.smembers('imgtag:untagged')))

    return render(request, 'tagger/tag_grid.html', {
        'page_obj': page_obj,
        'tag_data': tag_data,
        'all_tags_for_select2': all_tags_for_select2,
        'current_batch_tags': current_batch_tags,
        'total_untagged': total_untagged,
        'total_count': total_count,
    })
```

