    // Batch tags change ‚Äî clear selection with confirmation
    let lastBatchTags = $('#batch-tag-select').val() || [];
    $('#batch-tag-select').on('change', function() {
        const currentTags = $(this).val() || [];
        const tagsChanged = currentTags.sort().join(',') !== lastBatchTags.sort().join(',');
        if (tagsChanged && storedSelectedImages.length > 0) {
            const confirmClear = confirm("You are starting a new batch.\nPrevious selections will be cleared. Continue?");
            if (confirmClear) {
                storedSelectedImages = [];
                localStorage.setItem('selectedImages', '[]');
                $('.image-checkbox').prop('checked', false);
            } else {
                $('#batch-tag-select').val(lastBatchTags).trigger('change');
                return;
            }
        }
        lastBatchTags = currentTags;
        updatePaginationLinks();
    });
    // Pagination links update for batch_tags
    function updatePaginationLinks() {
        const currentBatchTags = $('#batch-tag-select').val();
        let tagsQueryString = '';
        if (currentBatchTags && currentBatchTags.length > 0) {
            tagsQueryString = '&batch_tags=' + currentBatchTags.map(encodeURIComponent).join(',');
        }
        $('.pagination a').each(function() {
            let href = $(this).attr('href');
            if (href) {
                href = href.replace(/&batch_tags=[^&]*/g, '');
                $(this).attr('href', href + tagsQueryString);
            }
        });
    }
    document.addEventListener("DOMContentLoaded", function () {
        updatePaginationLinks();
        $('#batch-tag-select').on('change', function() {
            updatePaginationLinks();
        });
    });
</script>
</body>
</html>
