### `parse_memories.py` (updated splitter)

```python
#!/usr/bin/env python3
import sys, re, json, uuid, argparse
from typing import List, Dict

LOCATION_HINTS = {
    "wilmington","delaware","dusseldorf","germany","rotterdam",
    "england","verdun","heathrow","states","united states","america",
    "vw","volkswagen","lunatic asylum"
}
PEOPLE_WHITELIST = {
    "Mom","Mum","Dad","Alec","Gisela Quante","Ken","Ken Estes",
    "state troopers","principal","gardener"
}
THEME_KEYWORDS = {
    "travel":{"flying","airport","across the atlantic","boeing","707","ferry","europe","england","germany","rotterdam","verdon","verdun","vw","volkswagen"},
    "school":{"elementary school","principal","teacher","first grade","second grade","cursive","retrained"},
    "family":{"mom","mum","dad","brother","family","parents"},
    "food":{"bread","ice cream","mustard soup","cook","cooking"},
    "discipline":{"beating","corporal punishment","principal's office"},
    "injury":{"stitches","hammer","machete","hit","eye","shin","hospital"},
    "bullying":{"bully","held back","anger","shame"},
    "adventure":{"climbing","exploring","parades","floats","candy"},
    "money":{"broke","departure tax","money"},
    "language":{"german","deutsch","will","english","teacher","shopping german","gekauft"},
}
COUNTRY_TITLES = {"US","U.S.","USA","U.S.A.","United States","Ireland","Germany","England","France"}
TITLE_EXCEPTIONS = {"I","We","My"}

AGE_PAT = re.compile(r"\b(age|aged)\s*(\d{1,2})\b", re.IGNORECASE)
YEAR_PAT = re.compile(r"\b(19\d{2}|20\d{2})\b")
PROPER_TOKEN = re.compile(r"\b([A-Z][\w\-\‚Äô']+)(?:\s+[A-Z][\w\-\‚Äô']+){0,2}\b")

def split_paragraphs(text: str, mode: str = "auto") -> List[str]:
    """Return list of units (paragraphs/lines/sentences) depending on mode."""
    # Normalize newlines and strip BOM/spaces
    text = text.replace("\r\n","\n").replace("\r","\n").strip()
    if mode == "auto":
        if "\n\n" in text:
            return [p.strip() for p in re.split(r"\n\s*\n+", text) if p.strip()]
        # no blank lines -> fall back to line mode
        mode = "line"

    if mode == "line":
        return [ln.strip() for ln in text.split("\n") if ln.strip()]

    if mode == "sentence":
        # naive sentence split: ., !, ? followed by space+capital or end of text
        parts = re.split(r'(?<=[.!?])\s+(?=[A-Z‚Äú"])', text)
        return [p.strip() for p in parts if p.strip()]

    raise ValueError(f"Unknown split mode: {mode}")

def find_ages(s: str):
    ages = []
    for m in AGE_PAT.finditer(s):
        try: ages.append(int(m.group(2)))
        except: pass
    return sorted(set(ages))

def find_years(s: str):
    return sorted({int(y) for y in YEAR_PAT.findall(s)})

def guess_locations(s: str):
    s_lc = s.lower()
    hits = set()
    for hint in LOCATION_HINTS:
        if hint in s_lc:
            # try capture Title Case span
            for m in re.finditer(r"([A-Z][a-zA-Z\-]+(?:,\s*[A-Z][a-zA-Z\-]+)?)", s):
                cand = m.group(1)
                if hint.split(",")[0] in cand.lower():
                    hits.add(cand.strip(", "))
            if not hits:
                hits.add(hint.title())
    for m in re.finditer(r"\b([A-Z][a-zA-Z\-]+(?:\s+[A-Z][a-zA-Z\-]+){0,2})\b", s):
        phrase = m.group(1).strip()
        if phrase in TITLE_EXCEPTIONS: continue
        if phrase.lower() in LOCATION_HINTS: hits.add(phrase)
        if any(ctry in phrase for ctry in COUNTRY_TITLES): hits.add(phrase)
    return sorted(hits)

def guess_people(s: str):
    people = set()
    for name in PEOPLE_WHITELIST:
        if re.search(rf"\b{re.escape(name)}\b", s):
            people.add(name)
    for m in PROPER_TOKEN.finditer(s):
        token = m.group(0).strip()
        if token in TITLE_EXCEPTIONS: continue
        if token.lower() in {"another","At","Back","In","When","Then","Soon","Finally","Not","Unfortunately"}: continue
        if token.isupper() and len(token)>3: continue
        if any(ch.isdigit() for ch in token): continue
        if token.lower() in LOCATION_HINTS: continue
        people.add(token)
    return sorted({p for p in people if len(p)>1 and not p.endswith(".")})

def detect_themes(s: str):
    s_lc = s.lower()
    themes = []
    for theme, words in THEME_KEYWORDS.items():
        if any(w in s_lc for w in words):
            themes.append(theme)
    return sorted(set(themes))

def make_record(paragraph: str, idx: int) -> Dict:
    return {
        "source": "memories",
        "version": "v1",
        "memory": {
            "id": str(uuid.uuid4()),
            "seq": idx,
            "text": paragraph,
            "ages": find_ages(paragraph),
            "years": find_years(paragraph),
            "locations": guess_locations(paragraph),
            "people": guess_people(paragraph),
            "themes": detect_themes(paragraph),
        }
    }

def main():
    ap = argparse.ArgumentParser(description="Parse memoir TXT to JSONL.")
    ap.add_argument("input")
    ap.add_argument("output")
    ap.add_argument("--split", choices=["auto","line","sentence"], default="auto",
                    help="How to chunk input. Default: auto (blank lines or per-line).")
    args = ap.parse_args()

    text = open(args.input, "r", encoding="utf-8-sig").read()
    chunks = split_paragraphs(text, mode=args.split)

    with open(args.output, "w", encoding="utf-8") as f:
        for i, chunk in enumerate(chunks, 1):
            f.write(json.dumps(make_record(chunk, i), ensure_ascii=False) + "\n")

    print(f"‚úÖ Wrote {len(chunks)} records to {args.output} (mode={args.split})")

if __name__ == "__main__":
    main()
```

